import Section from '../UI/Section';
import TaskForm from './TaskForm';
import useHttp from '../../hooks/use-http';

const NewTask = (props) => {

  const httpData = useHttp();

  const { isLoading, error, sendRequest: sendTaskRequest } = httpData;

  const enterTaskHandler = async (taskText) => {

    sendTaskRequest({
      url: 'https://react-course-http-request-default-rtdb.europe-west1.firebasedatabase.app/task.json', 
      method: 'POST', 
      body: { text: taskText }, 
      headers: {'Content-Type': 'application/json',}
    }, enterTaskHandler);

    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch(
        'https://react-course-http-request-default-rtdb.europe-west1.firebasedatabase.app/task.json',
        {
          method: 'POST',
          body: JSON.stringify({ text: taskText }),
          headers: {
            'Content-Type': 'application/json',
          },
        }
      );

      if (!response.ok) {
        throw new Error('Request failed!');
      }

      const data = await response.json();

      const generatedId = data.name; // firebase-specific => "name" contains generated id
      const createdTask = { id: generatedId, text: taskText };

      props.onAddTask(createdTask);
    } catch (err) {
      setError(err.message || 'Something went wrong!');
    }
    setIsLoading(false);
  };

  return (
    <Section>
      <TaskForm onEnterTask={enterTaskHandler} loading={isLoading} />
      {error && <p>{error}</p>}
    </Section>
  );
};

export default NewTask;
