//Import component and css
import React from "react";

//ExpenseFilter component in arrow method
const ExpenseFilter = (props) => {
    //Constant with all expenses
    const expenses = props.expenses;

    let yearsFilter = [];
    for(let i = 0; i < expenses.length; i++)
    {
        if(typeof expenses[i] == "undefined" || typeof expenses[i].date == "undefined" || expenses[i].date.length <= 0)
        {
            continue;
        }

        let date = expenses[i].date;
        date = new Date(date);
        let year = date.getFullYear();

        if(typeof year == "undefined" || year <= 0)
        {
            continue;
        }

        let inArrayYear = inArray(year, yearsFilter);

        if(inArrayYear)
        {
            continue;
        }

        yearsFilter.push(year);
    }

    yearsFilter.sort();


    function inArray(value, array)
    {
        if(typeof value == "undefined" || typeof array == "undefined")
        {
            return true;
        }

        for(let i = 0; i < array.length; i++) 
        {
            if(array[i] == value) 
            {
                return true;
            }
        }

        return false;
    };

    //Cycle all expense years
    const expenseYearsOption = yearsFilter.map(year => (<option key={year}>{year}</option>));

    return (
        <select>{expenseYearsOption}</select>
    );
};


//Export component
export default ExpenseFilter;
