//Import component and css
import React from "react";
import "../css/ExpenseForm.css";

const ExpenseForm = (props) => {
    const titleChangeHandler = (event) => {
        console.log(event.target.value);
        console.log(props.expenses);
        for(let i = 0; i < props.expenses.length; i++)
        {
            if(typeof props.expenses[i] == "undefined" || typeof props.expenses[i].title == "undefined" || props.expenses[i].title.length <= 0)
            {
                continue;
            }
        }
    };


  return <form>
      <div className="new-expense__controls">
          <div className="new-expense__control">
              <label for="newTitle">Title</label>
              <input type="text" id="newTitle" onChange={titleChangeHandler} required />
          </div>
          <div className="new-expense__control">
              <label for="newAmount">Amount</label>
              <input type="number" min="0.01" step="0.01" id="newAmount" required />
          </div>
          <div className="new-expense__control">
              <label for="newDate">Date</label>
              <input type="date" id="newDate" min="2019-01-01" max="2022-12-31" required />
          </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit">Add Expense</button>
      </div>
  </form>;
};

//Export component
export default ExpenseForm;
